import React, { Component } from "react";
import "popper.js";
import "bootstrap/dist/js/bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import "../index.css";
const data = require("../data/species.json");

var presentSpecies;

var commonName;
var latinName;
var description;
// var ?;
var diet;
var habitat;
// var ?;
// var ?;
// var ?;
var conservationStatus;
var images;
var map;
// var ?;

function changeSpecies(target) {
    console.log(target);
    presentSpecies = target;
    // console.log(selectId);
    // console.log(data);
}

class Species extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: ""
        };
    }

    updateState(newState) {
        this.setState({
            value: newState
        });
        presentSpecies = this.state.value;
        console.log(presentSpecies);
    }

    render() {
        return (
            <div
                className="container-fluid mb-2"
                style={{
                    position: "absolute",
                    height: "calc(100% - 72px)",
                    bottom: "8px"
                }}
            >
                <div
                    id="outer-grid"
                    className="h-100"
                    style={{
                        display: "grid",
                        gridTemplateColumns: "repeat(3, 1fr)",
                        gridTemplateRows: "repeat(5, 1fr)"
                    }}
                >
                    {/* Row 1 */}
                    <div
                        className="border h-100"
                        style={{
                            display: "grid",
                            gridTemplateRows: "1fr 0 1fr"
                        }}
                    >
                        <div id="common-name">
                            <h6 className="cell-name">Common Name: </h6>
                            <div className="cell-content dropdown">
                                <select
                                    id="common-name-selector"
                                    className="form-control"
                                    value={this.state.value}
                                    onChange={this.updateState}
                                >
                                    <option value="">
                                        Select by Common Name
                                    </option>
                                    {data.data.species.map(option => {
                                        return (
                                            <option
                                                value={option.index}
                                                key={option.id}
                                            >
                                                {option.common_name}
                                            </option>
                                        );
                                    })}
                                </select>
                            </div>
                        </div>
                        <hr className="m-0" />
                        <div id="latin-name">
                            <h6 className="cell-name">Latin Name: </h6>
                            {/* <div className="cell-content dropdown">
                                <select
                                    id="latin-name-selector"
                                    className="form-control"
                                    onChange={changeSpecies(
                                        this.props.value,
                                        1
                                    )}
                                >
                                    <option value="">
                                        Select by Latin Name
                                    </option>
                                    {data.data.species.map(option => {
                                        return (
                                            <option
                                                value={option.index}
                                                key={option.id}
                                            >
                                                {option.latin_name}
                                            </option>
                                        );
                                    })}
                                </select>
                            </div> */}
                        </div>
                    </div>
                    <div
                        className="border h-100"
                        style={{
                            display: "grid",
                            gridTemplateRows: "1fr 0 1fr 0 1fr"
                        }}
                    >
                        <div id="">abc</div>
                        <hr className="m-0" />
                        <div id="">abc</div>
                        <hr className="m-0" />
                        <div id="">abc</div>
                    </div>
                    <div id="conservation-status" className="border h-100">
                        abc
                    </div>
                    {/* Row 2 */}
                    <div id="description" className="border h-100">
                        abc
                    </div>
                    <div
                        id="images"
                        className="border h-100"
                        style={{
                            gridArea: "2 / 2 / 4 / 4"
                        }}
                    >
                        abc
                    </div>
                    {/* Row 3 */}
                    <div id="" className="border h-100">
                        abc
                    </div>
                    {/* Row 4 */}
                    <div id="diet" className="border h-100">
                        abc
                    </div>
                    <div
                        id="map"
                        className="border h-100"
                        style={{ gridArea: "4 / 2 / 6 / 3" }}
                    >
                        abc
                    </div>
                    <div
                        id=""
                        className="border h-100"
                        style={{ gridArea: "4 / 3 / 6 / 4" }}
                    >
                        abc
                    </div>
                    {/* Row 5 */}
                    <div id="habitat" className="border h-100">
                        abc
                    </div>
                </div>
                <style>
                    {
                        "\
                        #outer-grid div {\
                            padding: 4px;\
                        }\
                    "
                    }
                </style>
            </div>
        );
    }
}

export default Species;
